const themeToggle=document.getElementById("theme-toggle"),body=document.body,savedTheme=localStorage.getItem("theme");if(savedTheme)body.setAttribute("data-theme",savedTheme);else{const e=window.matchMedia&&window.matchMedia("(prefers-color-scheme: dark)").matches;body.setAttribute("data-theme",e?"dark":"light")}themeToggle.addEventListener("click",()=>{const t=body.getAttribute("data-theme"),e=t==="light"?"dark":"light";body.setAttribute("data-theme",e),localStorage.setItem("theme",e)})